###########################################################################
# Script de remédiation - Libération d'espace disque avant mise à niveau Windows 11
###########################################################################
# Auteur      : Emanuel DE ALMEIDA
# Site        : www.ctrlaltnod.com
# Description : Ce script affiche une notification Windows Toast à l'utilisateur
#               expliquant que l'espace disque est insuffisant pour la mise à 
#               niveau Windows 11. La notification propose 3 actions :
#               - Ouvrir le dossier Téléchargements pour révision manuelle
#               - Ouvrir la Corbeille pour révision manuelle
#               - Nettoyer automatiquement les deux (Téléchargements + Corbeille)
#
#               IMPORTANT : Les fichiers dans Téléchargements ne sont PAS 
#               sauvegardés automatiquement. L'utilisateur doit les déplacer 
#               vers OneDrive ou Documents avant le nettoyage si nécessaire.
#
#               Code de sortie :
#               - Exit 0 : Notification affichée avec succès
#               - Exit 1 : Échec de l'affichage de la notification
#
# Version     : 1.0
# Date        : 17 octobre 2025
###########################################################################

# Configuration de la notification
$Text_AppName   = "Information Espace Disque"  # Nom de l'application pour les notifications Windows

# Image d'en-tête optionnelle (format base64, 728px x 360px recommandé)
# Vous pouvez générer votre propre image sur : https://codebeautify.org/image-to-base64-converter
# Laissez vide "" pour ne pas afficher d'image
$Picture_Base64 = ""

# Chemins et variables
$Log           = "$env:ProgramData\\Microsoft\\IntuneManagementExtension\\Logs\\Remediate-LowDiskSpace.log"
$HeroImage     = "$env:TEMP\\HeroPicture.png" 
$UserProfile   = [Environment]::GetFolderPath('UserProfile')
$DownloadsPath = Join-Path $UserProfile "Downloads"

# Fonction pour écrire dans le journal de logs
function Write-Log {
    param (
        [Parameter(Mandatory = $true)]
        [string]$Message,
        
        [ValidateSet("INFO", "WARN", "ERROR")]
        [string]$Level = "INFO"
    )

    $timestamp = Get-Date -Format "yyyy-MM-dd HH:mm:ss"
    $logEntry = "[$timestamp][$Level] $Message"
    Add-Content -Path $Log -Value $logEntry
}

# Fonction pour enregistrer l'application de notification dans le registre Windows
function Register-NotificationApp {
    param(
        [string]$AppID,
        [string]$AppDisplayName
    )

    $AppRegPath = "HKCU:\\Software\\Classes\\AppUserModelId"
    $RegPath    = "$AppRegPath\\$AppID"
    $Notifications_Reg = "HKCU:\\SOFTWARE\\Microsoft\\Windows\\CurrentVersion\\Notifications\\Settings"

    Write-Log -Message "Enregistrement de l'application de notification : $AppDisplayName" -Level "INFO"

    try {
        if (-not (Test-Path -Path $RegPath)) {
            New-Item -Path $AppRegPath -Name $AppID -Force | Out-Null
        }
        New-Item -Path "$Notifications_Reg\\$AppID" -Force | Out-Null
        New-ItemProperty -Path "$Notifications_Reg\\$AppID" -Name "ShowInActionCenter" -Value 1 -PropertyType DWord -Force | Out-Null
        New-ItemProperty -Path $RegPath -Name "DisplayName" -Value $AppDisplayName -PropertyType String -Force | Out-Null
        New-ItemProperty -Path $RegPath -Name "ShowInSettings" -Value 0 -PropertyType DWord -Force | Out-Null
        New-ItemProperty -Path $RegPath -Name "IconUri" -Value "C:\\Windows\\ImmersiveControlPanel\\images\\logo.png" -PropertyType ExpandString -Force | Out-Null
        New-ItemProperty -Path $RegPath -Name "IconBackgroundColor" -Value 0 -PropertyType ExpandString -Force | Out-Null
        
        Write-Log -Message "✓ Application de notification enregistrée avec succès" -Level "INFO"
    } catch {
        Write-Log -Message "ERREUR lors de l'enregistrement de l'application : $_" -Level "ERROR"
    }
}

# Fonction pour créer des actions personnalisées via protocole
function Set-Action {
    param(
        [string]$Action_Name,
        [string]$CmdBody
    )
    
    $Main_Reg_Path = "HKCU:\\SOFTWARE\\Classes\\$Action_Name"
    $Command_Path  = "$Main_Reg_Path\\shell\\open\\command"
    $Script_Export_Path = "C:\\Windows\\Temp"
    
    Write-Log -Message "Création de l'action personnalisée : $Action_Name" -Level "INFO"
    
    if (-not (Test-Path $Script_Export_Path)) { 
        New-Item -ItemType Directory -Path $Script_Export_Path -Force | Out-Null 
    }

    $CmdFile = Join-Path $Script_Export_Path "$Action_Name.cmd"
    $CmdBody | Out-File $CmdFile -Force -Encoding ASCII

    try {
        New-Item $Command_Path -Force | Out-Null
        New-ItemProperty -Path $Main_Reg_Path -Name "URL Protocol" -Value "" -PropertyType String -Force | Out-Null
        Set-ItemProperty -Path $Main_Reg_Path -Name "(Default)" -Value "URL:$Action_Name Protocol" -Force | Out-Null
        Set-ItemProperty -Path $Command_Path -Name "(Default)" -Value $CmdFile -Force | Out-Null
        
        Write-Log -Message "✓ Action créée : $Action_Name -> $CmdFile" -Level "INFO"
    } catch {
        Write-Log -Message "ERREUR lors de la création de l'action $Action_Name : $_" -Level "ERROR"
    }
}

# Fonction pour afficher la notification Toast
function Show-ToastXml {
    param(
        [string]$XmlString,
        [string]$AppID
    )
    
    Write-Log -Message "Affichage de la notification Toast" -Level "INFO"
    
    try {
        [Windows.UI.Notifications.ToastNotificationManager, Windows.UI.Notifications, ContentType = WindowsRuntime] | Out-Null
        [Windows.Data.Xml.Dom.XmlDocument, Windows.Data.Xml.Dom.XmlDocument, ContentType = WindowsRuntime] | Out-Null
        
        $ToastXml = New-Object -TypeName Windows.Data.Xml.Dom.XmlDocument
        $ToastXml.LoadXml($XmlString)
        [Windows.UI.Notifications.ToastNotificationManager]::CreateToastNotifier($AppID).Show($ToastXml)
        
        Write-Log -Message "✓ Notification Toast affichée avec succès" -Level "INFO"
        return $true
    } catch {
        Write-Log -Message "ERREUR lors de l'affichage de la notification : $_" -Level "ERROR"
        return $false
    }
}

# ==================== DÉBUT DU SCRIPT ====================

Write-Log -Message "=== Remédiation de l'espace disque insuffisant ===" -Level "INFO"
Write-Log -Message "Script de remédiation démarré - ctrlaltnod.com" -Level "INFO"
Write-Log -Message "Utilisateur : $env:USERNAME | Ordinateur : $env:COMPUTERNAME" -Level "INFO"

# Étape 1 : Charger l'image d'en-tête (optionnel)
Write-Log -Message "Étape 1/5 : Chargement de l'image d'en-tête" -Level "INFO"

try {
    if ($Picture_Base64 -and $Picture_Base64 -ne "" -and $Picture_Base64 -ne "xx") {
        [byte[]]$Bytes = [Convert]::FromBase64String($Picture_Base64)
        [System.IO.File]::WriteAllBytes($HeroImage, $Bytes) | Out-Null
        Write-Log -Message "✓ Image d'en-tête chargée : $HeroImage" -Level "INFO"
    } else {
        $HeroImage = ""
        Write-Log -Message "Aucune image d'en-tête configurée" -Level "INFO"
    }
} catch {
    $HeroImage = ""
    Write-Log -Message "ERREUR lors du chargement de l'image : $_" -Level "WARN"
}

# Étape 2 : Créer les actions personnalisées (protocoles)
Write-Log -Message "Étape 2/5 : Création des actions personnalisées" -Level "INFO"

# Action 1 : Ouvrir le dossier Téléchargements
$Action_OpenDownloads = "OpenDownloads:"
$OpenDownloadsCmd = @"
start explorer.exe "$DownloadsPath"
"@

# Action 2 : Ouvrir la Corbeille
$Action_OpenRecycle = "OpenRecycle:"
$OpenRecycleCmd = @"
start explorer.exe shell:RecycleBinFolder
"@

# Action 3 : Nettoyer automatiquement (Corbeille + Téléchargements)
$Action_Clear = "CleanStorage:"
$CleanCmd = @"
powershell -command "Clear-RecycleBin -Force -ErrorAction SilentlyContinue"
powershell -command "if (Test-Path '$DownloadsPath') { Get-ChildItem -LiteralPath '$DownloadsPath' -Recurse -Force -File -ErrorAction SilentlyContinue | Remove-Item -Force -ErrorAction SilentlyContinue }"
"@

Set-Action -Action_Name OpenDownloads -CmdBody $OpenDownloadsCmd
Set-Action -Action_Name OpenRecycle   -CmdBody $OpenRecycleCmd
Set-Action -Action_Name CleanStorage  -CmdBody $CleanCmd

# Étape 3 : Enregistrer l'application de notification
Write-Log -Message "Étape 3/5 : Enregistrement de l'application de notification" -Level "INFO"

$AppID = $Text_AppName
Register-NotificationApp -AppID $AppID -AppDisplayName $Text_AppName

# Étape 4 : Créer le contenu de la notification Toast
Write-Log -Message "Étape 4/5 : Création du contenu de la notification" -Level "INFO"

# Textes de la notification en français
$Title   = "Plus d'espace nécessaire pour la mise à niveau Windows 11"
$Message = "Votre appareil ne dispose pas de suffisamment d'espace libre pour effectuer la mise à niveau. Vous pouvez libérer de l'espace en vidant la Corbeille et le dossier Téléchargements. Les fichiers dans Téléchargements ne sont sauvegardés nulle part. Si vous devez les conserver, déplacez-les vers OneDrive ou Documents avant le nettoyage."
$Advice  = "Si le nettoyage ne libère pas assez d'espace, le service IT vous assistera. Cela peut nécessiter des étapes manuelles et une courte interruption de travail."

# Créer les boutons d'action
$ActionsXml = @"
  <actions>
    <action activationType="protocol" arguments="$Action_OpenDownloads" content="Ouvrir Téléchargements" />
    <action activationType="protocol" arguments="$Action_OpenRecycle"   content="Ouvrir Corbeille" />
    <action activationType="protocol" arguments="$Action_Clear"         content="Nettoyer maintenant" />
  </actions>
"@

# Créer l'image d'en-tête (si disponible)
$HeroXml = ""
if ($HeroImage) { 
    $HeroXml = "<image placement=`"hero`" src=`"$HeroImage`"/>" 
    Write-Log -Message "Image d'en-tête intégrée à la notification" -Level "INFO"
}

# Construire le XML complet de la notification Toast
[xml]$Toast = @"
<toast scenario="reminder">
  <visual>
    <binding template="ToastGeneric">
      $HeroXml
      <text>$Title</text>
      <group>
        <subgroup>
          <text hint-style="body" hint-wrap="true">$Message</text>
        </subgroup>
      </group>
      <group>
        <subgroup>
          <text hint-style="body" hint-wrap="true">$Advice</text>
        </subgroup>
      </group>
    </binding>
  </visual>
  $ActionsXml
</toast>
"@

Write-Log -Message "✓ Contenu de la notification créé avec succès" -Level "INFO"

# Étape 5 : Afficher la notification Toast à l'utilisateur
Write-Log -Message "Étape 5/5 : Affichage de la notification à l'utilisateur" -Level "INFO"

try {
    $result = Show-ToastXml -XmlString $Toast.OuterXml -AppID $AppID
    
    if ($result) {
        Write-Log -Message "✓ Notification affichée avec succès à l'utilisateur" -Level "INFO"
        Write-Log -Message "L'utilisateur peut maintenant choisir comment libérer de l'espace" -Level "INFO"
        Write-Log -Message "=== Script de remédiation terminé avec succès ===" -Level "INFO"
        exit 0
    } else {
        Write-Log -Message "✗ Échec de l'affichage de la notification" -Level "ERROR"
        Write-Log -Message "=== Script de remédiation terminé avec erreur ===" -Level "ERROR"
        exit 1
    }
} catch {
    Write-Log -Message "✗ ERREUR critique lors de l'affichage de la notification : $_" -Level "ERROR"
    Write-Log -Message "=== Script de remédiation terminé avec erreur ===" -Level "ERROR"
    exit 1
}
